import React, { Component } from 'react';

class Form extends Component {
    constructor(props) {
        super(props);

        this.initialState = {
            inputItem: ''

        };

        this.state = this.initialState;
    }
    handleChange = event => {
        const {inputItem, value} = event.target;

        this.setState({
            [inputItem] : value
        });
    }
    submitForm = () => {
        this.props.handleSubmit(this.state);
        this.setState(this.initialState);
    }
    render() {
        const { inputItem } = this.state; 

        return (
            <form>

                <input 
                    type="text" 
                    name="inputItem" 
                    value={inputItem} 
                    onChange={this.handleChange} />
                     <input 
    type="button" 
    value="Submit" 
    onClick={this.submitForm} />
            </form>
        );
    }
}
export default Form;
